sys_script_include.script:
var MultiRowTableHelper = Class.create();
MultiRowTableHelper.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    /**
     * @description Function to parse out ServiceNow MultiRow Table Structure.
     *
     * @params {String:SysID} ritm - RITM SysID
     * @params {String:SysID} VariableSet - VariableSet Name
     */
    getTableRows: function (ritm, variableSet) {
        var dataObject = {};

        var myDataTable = new GlideRecord("sc_multi_row_question_answer");
        myDataTable.addQuery("variable_set", variableSet);
        myDataTable.addQuery("parent_id", ritm);
        myDataTable.orderBy("row_index");
        myDataTable.query();

        var currentRowID = myDataTable.row_index.toString();
        var currentRow = 1;

        while (myDataTable.next()) {
            if (currentRowID != myDataTable.row_index.toString()) {
                currentRow++;
            }

            dataObject.row = currentRow;
            dataObject.row.variableSet = [myDataTable.variable_set.toString()];
            dataObject.row.questions[myDataTable.item_option_new.toString()] = myDataTable.value.toString();
        }

        return dataObject;
    },

    type: 'MultiRowTableHelper'
});
